\textbf{Parts b and c combined:} (These are all the rules I can think
of).

\begin{enumerate}
\itemsep1pt\parskip0pt\parsep0pt
\item
  Addition rule:

  \begin{align*}
f(x) &= \frac{1}{2}x^6 + \frac{1}{2}x^6 &
  f'(x) &= 3x^5 + 3x^5
\end{align*}
\item
  Subtraction rule:

  \begin{align*}
f(x) &= 2x^6 - x^6 &
  f'(x) &= 12x^5 - 6x^5
\end{align*}
\item
  Product rule:

  \begin{align*}
f(x) &= x^2 \cdot x^4 &
  f'(x) &= 2x \cdot x^4 + 4x^3 \cdot x^2
\end{align*}
\item
  Quotient rule:

  \begin{align*}
f(x) &= \frac{x^7}{x} &
  f'(x) &= \frac{7x^6 \cdot x - x^7 \cdot 1}{x^2}
\end{align*}
\item
  Chain rule:

  \begin{align*}
f(x) &= (x^3)^2 &
  f'(x) &=  3x^2 \cdot 2(x^3)
\end{align*}
\item
  Log-diff:

  \begin{align*}
\ln(f(x)) &= 6 \ln(x)& 
  f'(x) &= \frac{6}{x}\cdot x^6
\end{align*}
\item
  Exp-chain rule:

  \begin{align*}
f(x) &= \exp[6\ln(x)]&
  f'(x) &= \frac{6}{x} \cdot \exp[6\ln(x)]
\end{align*}
\item
  Limit definition of derivative:

  \begin{align*}
f'(x) &= \lim_{h\rightarrow 0} \frac{(x+h)^6 - x^6}{h} & f'(x) &= \lim_{h\rightarrow 0} \frac{6x^5h + 15x^4h^2 + \ldots + h^6}{h}\\
  f'(x) &= \lim_{a\rightarrow x} \frac{a^6 - x^6}{a-x} & f'(x) &= \lim_{a\rightarrow x} \frac{(a-x)(a^5 + a^4x + \ldots x^5)}{(a-x)}
\end{align*}
\end{enumerate}

